mapper.delete:
delete from user_report
where content_id = #{content_id}
and user_id = #{user_id}

mapper.select:
select distinct report_category
from user_report
where content_id=#{content_id};

select count(report_category)
	from user_report
	where content_id=#{content_id} and report_category=#{key};

select  report_val
from user_report
where content_id=#{content_id} and report_category='기타';
